import React from "react";
import memedata from "../memedata";

export default function Meme(){
    const [memeImage,setMemeImage] = React.useState("");
    function getMemeImage(){
        const memes_array = memedata.data.memes;
        const randomNum = Math.floor(Math.random()*memes_array.length);
        const url = memes_array[randomNum].url;
        setMemeImage(url);

        console.log(memeImage);
    }
    return (
        <main>
            <div className="form">
                <input className="form-inputs" type="text" placeholder="Enter top text"/>
                <input className="form-inputs" type="text" placeholder="Enter bottom text"/>
                <button className="form-button" onClick={getMemeImage}>Get a new meme image</button>
            </div>
            <img src={memeImage} />
        </main>
    )
}
